mapper.select:
SELECT DATE_FORMAT(j.sendtime , '%Y-%m-%d %H:%i') adddate, j.article_id, a.title, j.content, u.id, u.nickname, CASE j.send_flag WHEN 0 THEN '待发送' WHEN 1 THEN '发送中' WHEN 2 THEN '发送成功' WHEN 3 THEN '发送失败' WHEN 4 THEN '状态未明' WHEN 5 THEN '测试不发送' ELSE '其他' END send_flag from sh_jnl_article_comment j, sh_admin_user u , sh_article a where 1 = 1 and a.id=j.article_id and date_format(j.adddate,'%Y-%m-%d') = #{addDate}
and j.user_id = u.id
and j.user_id = #{userId}
and j.article_id=#{articleid}
and j.content like '%${content}%'
order by j.adddate desc

SELECT count(*) from sh_jnl_article_comment j, sh_admin_user u , sh_article a where 1 = 1 and a.id=j.article_id and date_format(j.adddate,'%Y-%m-%d') = #{addDate}
and j.user_id = u.id
and j.user_id = #{userId}
and j.article_id=#{articleid}
and j.content like '%${content}%'
and j.send_flag='2'
